
public class Java_Example013 {

	/**
	 * @param args: Java increment operator ++ increases its operand's value by one
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 5;
		int j= 4;
		int k =13;
		i++;// This demonstrate i = i+1
		k= 	j;

		System.out.println("The value of i:"+ i);
		System.out.println("The value of j:"+j);
		++j;
		++k;
		System.out.println("********************");
		
		System.out.println("The value After Preincrement of  j:"+j);
		System.out.println("The value After Preincrement of  k:"+k);
	}

}
