
public class Java_Example016 {

	/**
	 * @param args: Modulus operator gives the reminder of the division 
	 * of floating point and integer types.
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Java modulus operator reminder Example");
		int i =50;
		double d = 20;
		System.out.println("Modulus of i:"+ i%10);
		System.out.println("Modulus of d:"+ d%10);
		
		
	}

}
