

public class Java_Example024 {
	/** 
	 * @param args: This Example will explain you how to get length of array.
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char[] array = new char[5];
		array[0]= 'a';
		array[1]= 'e';
		array[2]= 'i';
		array[3]= 'o';
		array[4]= 'u';
		System.out.println("The array length is:"+ array.length);
		
	}

}
